    public function getSourceContext()
    {
        return new Source("{% extends 'layout.html.twig' %}

{% block title %}USER index{% endblock %}

{% block body %}
    <h1>USER index</h1>

    <table class=\"table\">
        <thead>
        <tr>
            <th>Id</th>
            <th>Email</th>
            <th>role</th>
            <th>avatar</th>
        </tr>
        </thead>
        <tbody>
        {% for user in users %}
            <tr>
                <td>{{ user.id }}</td>
                <td>{{ user.email }}</td>
                <td>{{ user.roles[0] }}</td>
                <td>{% if user.avatar %}
                    <img src=\"{{ user.avatar }}\" alt=\"Avatar\" style=\"max-width: 50px; height: auto;\">
                    {% else %}
                    <p>No avatar available.</p>
                {% endif %}
                </td>

                <td><a href=\"{{ url('app_user_blog_index', {'_switch_user': user.email}) }}\">Войти под пользователем</a></td>
            </tr>
        {% else %}
            <tr>
                <td colspan=\"3\">no records found</td>
            </tr>
        {% endfor %}
        </tbody>
    </table>

{% endblock %}

", "user/index.html.twig", "/var/www/symfonytask/task_project/templates/user/index.html.twig");
